<style lang="scss" scoped>
@import "../../css/mixin.scss";
.anyi-radio {
  height: rem(42);
  width: rem(42);
  background: url("data:image/png;base64,")
    no-repeat left center;
  background-size: rem(32) rem(32);
  &.checked {
    background: url("data:image/png;base64,")
      no-repeat left center;
    background-size: rem(32) rem(32);
  }
}
</style>
<template>
  <div class="anyi-radio" @click="_radioChange" :class="[value ? 'checked' : '']">
  </div>
</template>
<script>
export default {
  props: {
    value: ""
  },
  methods: {
    _radioChange() {
      var isChecked = !this.value;
      this.$emit("input", isChecked);
    }
  }
};
</script>